-- Índices para optimizar búsquedas
ALTER TABLE datosipsndx.citas
    ADD INDEX idx_confirmacion (Confirmada, FechaConfirmacion),
    ADD INDEX idx_medio_confirmacion (MedioConfirmacion, IdMedioConfirmacion),

-- Comentario: Este script agrega campos para:
-- 1. Control de confirmación de citas
-- 2. Registro del medio de comunicación usado
-- 3. Trazabilidad del mensaje o llamada específica
-- Los índices ayudarán en consultas de reportes y seguimiento

-- --------------------------------------------------------
-- Tabla: cups_procedimientos
-- Descripción: Catálogo de procedimientos y exámenes CUPS
-- --------------------------------------------------------

CREATE TABLE datosipsndx.cups_procedimientos (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT 'Identificador único del procedimiento',
    codigo_cups VARCHAR(10) NOT NULL COMMENT 'Código CUPS del procedimiento',
    nombre VARCHAR(255) NOT NULL COMMENT 'Nombre del procedimiento',
    descripcion TEXT COMMENT 'Descripción detallada del procedimiento',
    especialidad_id INT COMMENT 'ID de la especialidad relacionada',
    servicio_id INT COMMENT 'ID del servicio relacionado',
    preparacion TEXT COMMENT 'Instrucciones de preparación para el paciente',
    direccion TEXT NOT NULL COMMENT 'Dirección donde se realiza el procedimiento',
    video_url VARCHAR(255) COMMENT 'URL del video instructivo',
    audio_url VARCHAR(255) COMMENT 'URL del audio instructivo',
    tipo ENUM('procedimiento', 'examen', 'nocturno') NOT NULL DEFAULT 'examen' COMMENT 'Tipo de servicio',
    horario_especifico_id INT NULL COMMENT 'ID de horario específico si aplica',
    activo TINYINT(1) NOT NULL DEFAULT 1 COMMENT 'Estado del procedimiento (1: activo, 0: inactivo)',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT 'Fecha de creación',
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Fecha de última actualización',

    CONSTRAINT uk_codigo_cups UNIQUE (codigo_cups)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Índices para optimizar búsquedas
ALTER TABLE datosipsndx.cups_procedimientos
	ADD INDEX idx_cups_codigo (codigo_cups),
	ADD INDEX idx_cups_horario (horario_especifico_id),
	ADD INDEX idx_cups_activo (activo);

-- Comentarios sobre la tabla cups_procedimientos:
-- 1. codigo_cups: Código único del procedimiento según clasificación CUPS
-- 2. espacios_requeridos: Indica cuántos espacios de agenda ocupa (1-4)
-- 3. horario_especifico_id: NULL significa que se puede agendar en cualquier horario
-- 4. flujo_asignacion_id: NULL significa que solo requiere orden médica
-- 5. Se incluyen campos de auditoría (created_at, updated_at)
-- 6. Se agregan índices para optimizar las consultas más comunes

-- --------------------------------------------------------
-- Tabla: cup_medico
-- Descripción: Médicos asignados a CUPS
-- --------------------------------------------------------
CREATE TABLE datosipsndx.cup_medico (
    id INT AUTO_INCREMENT PRIMARY KEY,
    cup_id INT NOT NULL,
    doctor_documento VARCHAR(30) NOT NULL,
    doctor_nombre_completo VARCHAR(150) NOT NULL,
    activo TINYINT(1) NOT NULL DEFAULT 1,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    UNIQUE KEY unique_cup_doctor (cup_id, doctor_documento),
    FOREIGN KEY (cup_id) REFERENCES cups_procedimientos(id)
);

ALTER TABLE datosipsndx.cup_medico
	ADD INDEX idx_cup_medico (cup_id),
    ADD INDEX idx_doctor_documento (doctor_documento);

-- Comentarios sobre la tabla cup_medico:
-- 1. cup_id: ID del procedimiento CUPS
-- 2. doctor_documento: Documento del médico
-- 3. doctor_nombre_completo: Nombre completo del médico
-- 4. activo: Estado del médico para el cup (1: activo, 0: inactivo)
-- 5. created_at: Fecha de creación

-- --------------------------------------------------------
-- Tabla: cups_horarios_especificos
-- Descripción: Horarios específicos permitidos para CUPS
-- --------------------------------------------------------

CREATE TABLE datosipsndx.cups_horarios_especificos (
    id INT AUTO_INCREMENT PRIMARY KEY COMMENT 'Identificador único del horario específico',
    cup_id INT NOT NULL COMMENT 'ID del procedimiento CUPS',
    hora TINYINT NOT NULL COMMENT 'Hora del día (0-23)',
    activo TINYINT(1) NOT NULL DEFAULT 1 COMMENT 'Estado del horario (1: activo, 0: inactivo)',
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP COMMENT 'Fecha de creación',
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT 'Fecha de última actualización',

    CONSTRAINT fk_horario_cup FOREIGN KEY (cup_id) REFERENCES cups_procedimientos(id) ON DELETE CASCADE,
    CONSTRAINT chk_hora_valida CHECK (hora BETWEEN 0 AND 23),
    CONSTRAINT uk_cup_hora UNIQUE (cup_id, hora)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- Índices para optimizar búsquedas
ALTER TABLE datosipsndx.cups_horarios_especificos
	ADD INDEX idx_horario_cup (cup_id);

-- Comentarios sobre la tabla cups_horarios_especificos:
-- 1. Permite asignar múltiples horas a un mismo CUP
-- 2. La hora representa el inicio del bloque (ej: 7 significa de 7:00 a 7:59)
-- 3. La restricción UNIQUE evita duplicados de hora para el mismo CUP
-- 4. CASCADE en FK permite eliminar horarios al eliminar un CUP
-- 5. CHECK asegura que las horas estén entre 0 y 23


-- --------------------------------------------------------
-- Usuario dedicado para la aplicación
-- --------------------------------------------------------

